<?phprequire_once('settings.php');$urlinfo = $_SERVER['REQUEST_URI'];$urlparts = explode('/', explode('?', $urlinfo)[0]);$params = [];foreach ($urlparts as $v) {	if ($v != 'index.php' && $v != '') {		$params[] = $v;	}}$controller = isset($params[0]) ? array_shift($params) : 'index';$action = isset($params[0]) ? array_shift($params) : 'index';define('CONTROLLER', $controller);define('ACTION', $action);define('PARAMS', $params);$controller = "c_{$controller}";$action = 'action_' . $action;if (!class_exists($controller)) $controller = 'c_index';$controller_obj = new $controller();$controller_obj->Request($action);